import axios from "axios";

const options = {
  method: 'POST',
  url: 'https://api.uzapi.com.br/{username}/v1/{phone_number_id}/messages',
  headers: {
    Authorization: 'Bearer REPLACE_BEARER_TOKEN',
    'content-type': 'application/json'
  },
  data: {
    to: 5543996254177,
    delayMessage: 3,
    context: {message_id: 'some-message-id'},
    editMessageID: '3EB08CFBE0590679EA2E06',
    mentionedJID: ['5543996254177@s.whatsapp.net', '5543999876543@s.whatsapp.net'],
    type: 'text',
    delayTyping: 5,
    text: {body: 'Ol√°, tudo bem? Em que posso ajudar?'}
  }
};

axios.request(options).then(function (response) {
  console.log(response.data);
}).catch(function (error) {
  console.error(error);
});

const http = require("https");

const options = {
  "method": "POST",
  "hostname": "api.uzapi.com.br",
  "port": null,
  "path": "/{username}/v1/{phone_number_id}/messages",
  "headers": {
    "Authorization": "Bearer REPLACE_BEARER_TOKEN",
    "content-type": "application/json"
  }
};

const req = http.request(options, function (res) {
  const chunks = [];

  res.on("data", function (chunk) {
    chunks.push(chunk);
  });

  res.on("end", function () {
    const body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.write(JSON.stringify({
  to: 5543996254177,
  delayMessage: 3,
  context: {message_id: 'some-message-id'},
  editMessageID: '3EB08CFBE0590679EA2E06',
  mentionedJID: ['5543996254177@s.whatsapp.net', '5543999876543@s.whatsapp.net'],
  type: 'text',
  delayTyping: 5,
  text: {body: 'Ol√°, tudo bem? Em que posso ajudar?'}
}));
req.end();

